// mobile-nav
//------------------------------------------------
.nav-wrap
  position fixed
  bottom 220px
  right 0
  max-width 50px
  visibility hidden
  opacity 0
  transition: all .3s;
  -webkit-transition: all .3s;
  -moz-transition: all .3s;
  &.is-visible
    visibility visible
    opacity 1
    pointer-events auto

.nav
  height 30px
  width 30px

.site-nav
  position: absolute;
  height: 30px;
  width: 30px;
  background-color: rgb(122, 130, 136);
  color: rgb(255, 255, 255);
  cursor: pointer;
  padding: 0px 0.3rem;
  border-width: 0px;
  border-style: initial;
  border-color: initial;
  border-image: initial;
  outline: none;

.navicon
  position: relative;
  width: 18px;
  height: 3px;
  background: rgb(255, 255, 255);
  margin: auto;
  transition: all 0.3s ease 0s;
  &::before
    content: "";
    position: absolute;
    top: -8px;
    left: 0px;
    width: 18px;
    height: 3px;
    background: rgb(255, 255, 255);
    transition: all 0.3s ease 0s;
  &::after
    content: "";
    position: absolute;
    bottom: -8px;
    left: 0px;
    width: 18px;
    height: 3px;
    background: rgb(255, 255, 255);
    transition: all 0.3s ease 0s;

.nav-open .navicon
  background: transparent;
  &::before
    transform-origin: 50% 50%;
    top: 0px;
    left: -3px;
    width: 25px;
    transform: rotate3d(0, 0, 1, 45deg);
  &::after
    transform-origin: 50% 50%;
    top: 0px;
    left: -3px;
    width: 25px;
    transform: rotate3d(0, 0, 1, -45deg);

.nav_items
  visibility: hidden;
  opacity: 0;
  position: absolute;
  right: 45px;
  top: -20px;
  margin: 0;
  box-shadow: rgba(0, 0, 0, 0.25) 0px 0px 10px;
  padding: 5px;
  border-width: 1px;
  border-style: solid;
  border-color: rgb(242, 243, 243);
  border-image: initial;
  border-radius: 4px;
  background: rgb(255, 255, 255);
  list-style: none;
  transition: all ease-in-out 0.2s;
  li
    display: block;
    border-bottom: 1px solid rgb(242, 243, 243);
    a
      display: block;
      color: rgb(122, 130, 136);
      font-size: 1.6rem;
      margin: 0px;
      padding: 10px 20px;
      text-decoration: none;
      transition: all 0.2s ease-in-out 0s;
      &:hover
        color: rgb(92, 98, 102);
        background: rgb(222, 224, 225);
  &::before
    content: "";
    position: absolute;
    top: 20px;
    right: -10px;
    width: 0px;
    display: block;
    z-index: 0;
    border-style: solid;
    border-width: 10px 0px 10px 10px;
    border-color: transparent transparent transparent white;
  &::after
    clear: both;
    content: "";
    display: table;

.nav-open .nav_items
  visibility visible
  opacity 1

.cd-top
  display block
  width auto
  height auto
  i
    display: block;
    margin-top: 3px;
    width: 30px;
    height: 30px;
    background-color: #7a8288;
    color: #fff;
    text-align: center;
    text-decoration: none;
    font-size: 16px;
    line-height: 29px;
    cursor: pointer;


